#define ll long long
class Solution {
public:
    ll mod = 1e9 + 7, n;
    vector <ll> v;
    vector <vector<ll>> dp;

    ll rec(int i, int prev){
        if(i == n) return 1;
        ll ans = 0;
        if(dp[i][prev] + 1) return dp[i][prev];
        for(int k = prev; k<=v[i]; k++){
            if(i == 0){
                ans = (ans + rec(i+1, k)) % mod;
            }
            else{
                if(v[i-1] - prev >= v[i] - k) ans = (ans + rec(i+1, k)) % mod;
            }
        }
        return dp[i][prev] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        n = nums.size(); for(auto i : nums) v.push_back(i);
        int maxm = 0; for(auto i : nums) maxm = max(maxm, i);
        dp.clear(); dp.resize(n); for(int i = 0; i<n; i++) dp[i].resize(maxm+10, -1);
        return rec(0, 0);
    }
};
