#include <bits/stdc++.h> 
using namespace std;
typedef long long ll;

class Solution {
public:
    vector<vector<vector<ll>>> dp;
    int mod;
    ll f(ll i, ll prev1, ll prev2, vector<int> &nums){
        ll n=nums.size();
        if(i>=n) return 1;
        if(dp[i][prev1][prev2]!=-1) return dp[i][prev1][prev2];
        ll sum=nums[i],ans=0;
        for(int j=0;j<=50;j++){
            ll num1=j,num2=sum-j;
            if(num2>=0 && num2<=50 && (i==0 || (num1>=prev1 && num2<=prev2))){
                ans+=f(i+1,num1,num2,nums)%mod;
                ans%=mod;
            }
        }
        return dp[i][prev1][prev2]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        ll n=nums.size(); mod=1e9 +7; dp.resize(n+1,vector<vector<ll>>(51,vector<ll>(51,-1)));
        ll ans=f(0,0,0,nums)%mod;
        return ans;
    }
};
