#define ll long long 
const ll MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vector<vector<ll>> dp(n + 1, vector<ll>(55, -1));
        function<ll(int, int)> f = [&](int i, int prev) {
            if (i == n) return 1LL;
            if (dp[i][prev] != -1) return dp[i][prev];
            ll ans = 0;
            ll prev2 = i ? a[i - 1] - prev : 2000;
            for (int j = prev; j <= a[i]; j++) {
                int k = a[i] - j;
                if (prev2 < k) continue;
                ans = (ans + f(i + 1, j)) % MOD;
            }
            return dp[i][prev] = ans;
        };

        ll ans = f(0, 0);
        return ans;
    }
};
