class Solution {
public:
    long long mod = 1e9 + 7;

    int solve(int i, int p1, int p2, vector<int>& nums, vector<vector<vector<int> > >& dp){
        if(i == nums.size()){
            return 1;
        }
        if(dp[i][p1][p2] != -1){
            return dp[i][p1][p2];
        }

        int count = 0;

        for (int ele1 = p1; ele1 <= nums[i]; ele1++) {
            int ele2 = nums[i] - ele1;
            int curr = 0;
            if (ele2 <= p2){
                curr = solve(i + 1, ele1, ele2, nums, dp);
            }
            count = (count + curr) % mod;
        }

        return dp[i][p1][p2] = count;
    }

    int countOfPairs(vector<int>& nums){
        int n = nums.size();
        vector<vector<vector<int> > > dp(n, vector<vector<int> > (51, vector<int> (51, -1)));

        return solve(0, 0, 50, nums, dp);
    }
};
