class Solution {
public:
    int MOD = 1e9+7;
    int goFind(int idx, int n1, int n2, vector<int> &nums, vector<vector<vector<int>>> &dp) {
        if(idx == nums.size()) {
            return 1;
        }
        if(dp[idx][n1][n2] != -1) {
            return dp[idx][n1][n2];
        }

        int total = 0;
        for(int i=n1;i<=50;i++) {
            int num1 = i;
            int num2 = nums[idx]-num1;
            if(num2 >= 0 && num2 <= n2) {
                total = (total + goFind(idx+1, i, num2, nums, dp))%MOD;
            }
        }
        return dp[idx][n1][n2] = total % MOD;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size()+1, vector<vector<int>> (51, vector<int> (51, -1)));
        int total = 0;
        for(int i=0;i<=50;i++) {
            int num1 = i;
            int num2 = nums[0]-num1;
            if(num2 >= 0) {
                total = (total + goFind(1, i, num2, nums, dp))%MOD;
            }
        }
        return total;
    }
};
