MOD = 10**9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_value = 50
        dp = [[[0] * (max_value + 1) for _ in range(max_value + 1)] for _ in range(n + 1)]

        for j in range(max_value + 1):
            k = nums[0] - j
            if 0 <= k <= max_value:
                dp[1][j][k] = 1

        for i in range(1, n):
            for j in range(max_value + 1):
                for k in range(max_value + 1):
                    if dp[i][j][k] > 0:
                        for nj in range(j, max_value + 1):
                            nk = nums[i] - nj
                            if 0 <= nk <= max_value and nk <= k:
                                dp[i + 1][nj][nk] = (dp[i + 1][nj][nk] + dp[i][j][k]) % MOD

        result = 0
        for j in range(max_value + 1):
            for k in range(max_value + 1):
                result = (result + dp[n][j][k]) % MOD

        return result
