typedef long long ll;
class Solution {
public:
    const int MOD = 1e9 + 7;
    ll sum(ll idx,ll prev1,ll prev2,vector<int> &nums,vector<vector<vector<ll>>> &dp) {
        if(idx==nums.size()) {
            return 1;
        }
        auto &x=dp[idx][prev1][prev2];
        if(x!=-1) {
            return x;
        }
        ll ans=0;
        for(int i=0;i<=nums[idx];i++) {
            if(i>=prev1&&nums[idx]-i<=prev2) {
                ans+=sum(idx+1,i,nums[idx]-i,nums,dp);   
                ans%=MOD;
            }
        }
        ans%=MOD;
        return x = ans;
    }
    int countOfPairs(vector<int>& nums) {
        ll ans=0;
        vector<vector<vector<ll>>> dp(nums.size()+10,vector<vector<ll>>(51,vector<ll>(51,-1)));
        for(int i=0;i<=nums[0];i++) {
            ans+=sum(1,i,nums[0]-i,nums,dp);
            ans%=MOD;
        }
        ans%=MOD;
        return ans;
    }
};
