class Solution:

    def countOfPairs(self, nums: List[int]) -> int:

        MOD = 10**9 + 7

        @cache
        def dfs(idx, limit0, limit1):
            if idx == len(nums):
                return 1
            ret = 0
            down = max(limit0, nums[idx] - limit1)
            up = nums[idx]
            for nx in range(down, up + 1):
                ret += dfs(idx + 1, nx, nums[idx] - nx)
            return ret % MOD

        return dfs(0, 0, nums[0])
