class Solution {
    int memo[2001][51][51];
    int mod = 1e9+7;
public:
    int dp(vector<int>& nums, int idx, int ap, int bp)
    {
        if(idx >= nums.size())
            return 1;

        if(memo[idx][ap][bp] != -1)
            return memo[idx][ap][bp];

        int ans = 0;

        for(int i=0;i<=nums[idx];i++)
        {
            if(ap <= i && bp >= nums[idx]-i)
                ans += dp(nums, idx+1, i, nums[idx]-i);

            ans %= mod;
        }

        return memo[idx][ap][bp] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(memo, -1, sizeof(memo));

        return dp(nums, 0, 0, 50);
    }
};
