class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        mod = int(1e9 + 7)
        @lru_cache(None)
        def go(i, p1, p2):
            # print('  '* i, p1, p2)
            if i == n:
                return 1
            res = 0
            for pp1 in range(p1, nums[i] + 1):
                pp2 = nums[i] - pp1
                # print('  '* i, p1, p2, pp1, pp2)
                if pp2 <= p2: 
                    res = (res + go(i + 1, pp1, pp2)) % mod
            # print('  '* i, p1, p2, 'res', res)
            return res

        res = 0
        for p1 in range(nums[0] + 1):
            res = (res + go(1, p1, nums[0] - p1)) % mod
        return res
