class Solution {
    int mod = (int)1e9+7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][51];
        for(int i=0;i<n;i++) for(int j=0;j<51;j++) dp[i][j] = -1;
        return count(nums, 0, 0, dp);
    }

    private int count(int[] nums, int ind, int prev, int[][] dp) {
        if(ind==nums.length) return 1;
        if(dp[ind][prev]!=-1) return dp[ind][prev];
        int ans = 0;
        for(int i=prev;i<=nums[ind];i++) {
            int a = i, b = nums[ind] - a;
            if(ind==0) {
                ans = (ans + count(nums, ind+1, a, dp))%mod;
            } else {
                if(b<=nums[ind-1]-prev) {
                    ans = (ans + count(nums, ind+1, a, dp))%mod;
                }
            }
        }

        return dp[ind][prev] = ans;
    }
}
