class Solution {
public:
    vector<vector<int>> dp;
    int mod = 1e9+7;
    int solve(vector<int> &nums , int i , int p , int x)
    {
        if(i == nums.size())
        {
            return 1;
        }
        if(dp[i][p] != -1)
            return dp[i][p];

        int ans = 0;
        for(int j = p ; j<=nums[i];j++)
        {
            if(x >= nums[i]-j)
                ans = (ans%mod + solve(nums , i+1 , j , nums[i]-j)%mod)%mod;
        }

        return dp[i][p] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size() , vector<int>(51 , -1));

        return solve(nums , 0 , 0 , INT_MAX);
    }
};
