class Solution {
public:
    // where you are, what number you are?

    int countOfPairs(vector<int>& A) {  // !
        ll n = A.size();
        ll k = *max_element(all(A)) + 1;

        VVLL dp(n, VLL(k));
        VLL pre(k + 1);

        rep(i, 0, n) {
            rep(j, 0, k) {
                // end here

                if (j > A[i]) {
                    continue;
                }

                ll opp = A[i] - j;
                if (opp < 0 || opp > A[i]) {
                    continue;  // 0
                }



                if (i == 0) {
                    dp[i][j] = 1;
                } else {
                    ll lo = 0, hi = j + 1;

                    upmin(hi, A[i - 1] - opp + 1);
                    upmax(hi, 0);
                    dp[i][j] = pre[hi];
                }
            }

            pre[0] = 0;
            rep(j, 0, k) {
                pre[j + 1] = (pre[j] + dp[i][j]) % mod;
            }
        }

        ll ans = 0;
        rep(j, 0, k) {
            ans += dp[n - 1][j];
            ans %= mod;
        }
        return ans;
    }
};
