class Solution {
public:
    int MOD = 1e9 + 7;
    vector<vector<int>> memo;
    int dp(int i, int last, const vector<int>& nums) {
        if (i == nums.size()) {
            return 1;
        }
        if (memo[i][last] != -1) {
            return memo[i][last];
        }

        int ans = 0;
        for (int x = last; x <= nums[i]; ++x) {
            int y = nums[i] - x;
            if (i == 0 || y <= nums[i-1] - last) {
                ans = (ans + dp(i + 1, x, nums)) % MOD;
            }
        }

        memo[i][last] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memo = vector<vector<int>>(n, vector<int>(200, -1));
        return dp(0, 0, nums);
    }
};
