class Solution {
public:
    vector<int> mn;
    vector<int> ot;
    vector<int> val;
    int mod=1e9 + 7;
    void print(){
        for(auto it: val){
            cout<<it<<" ";
        }
        cout<<"\n";
    }
    long dp[2001][51][51];
    long helper(vector<int> &nums, int i, int premn, int premx){
        if(i==nums.size()){
            // print();
            return 1;
        }

        if(dp[i][premn][premx]!=-1){
            return dp[i][premn][premx];
        }
        long cnt=0;
        for(int j=premn;j<=nums[i];++j){
            int f=j, sec=nums[i]-j;
            if(f>=premn && sec<=premx){
                cnt+=helper(nums, i+1, f, sec);
            }
        }
        cnt%=mod;
        return dp[i][premn][premx]=cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        mn=vector<int> (n+1, 0);
        mn[n-1]=nums[n-1];
        memset(dp, -1, sizeof(dp));
        for(int i=n-2;i>-1;--i){
            mn[i]=min(mn[i+1], nums[i]);
        }
        return helper(nums, 0, 0, nums[0]);
    }
};
