class Solution {
public:
    int f[2010][52][52];
    int countOfPairs(vector<int>& nums) {
        memset(f,0,sizeof f);
        int n=nums.size();
        int mod=1e9+7;
        f[0][0][0]=1;
        for(int i=1; i<=n; i++){
            for(int a=0; a<=nums[i-1]; a++){
                int b=nums[i-1]-a;
                if(i==1){
                    f[i][a][b]=1;
                    continue;
                }
                for(int pa=0; pa<=nums[i-2]; pa++){
                    int pb=nums[i-2]-pa;
                    if(a<pa || b>pb){
                        continue;
                    }
                    f[i][a][b]+=f[i-1][pa][pb];
                    f[i][a][b]%=mod;
                }
            }
        }
        int res=0;
        for(int i=0; i<=nums[n-1]; i++){
            int j=nums[n-1]-i;
            res=(res+f[n][i][j])%mod;
        }
        return res;
    }
};
