class Solution {
public:
    long long dp[2001][51][51];
    long long fun(int i, vector<int> &a, int al, int bl){
        if(i == a.size()) return 1;
        if(dp[i][al][bl] != -1) return dp[i][al][bl];
        long long ans = 0;
        for(int k = al; k <= a[i]; k++){
            if(a[i] - k > bl) continue;
            ans += fun(i + 1, a, k, a[i] - k);
            ans %= 1000000007;
        }
        return dp[i][al][bl] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return fun(0, nums, 0, 50);
    }
};
