class Solution {
public:
    const int mod = 1e9 + 7;
int countOfPairs(vector<int>& nums) {
    int n = nums.size();
    vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, 0)));
    for (int i = 0; i <= nums[0]; i++) {
        int y = nums[0] - i;
        dp[0][i][y] = 1;
    }
    for (int i = 1; i < n; i++) {
        for (int x = 0; x <= 50; x++) {
            for (int y = 0; y <= 50; y++) {
                if (dp[i-1][x][y] > 0) {
                    for (int newx = x; newx <= nums[i]; newx++) {
                        int newy = nums[i] - newx;
                        if (newy <= y) {
                            dp[i][newx][newy] = (dp[i][newx][newy] + dp[i-1][x][y]) % mod;
                        }
                    }
                }
            }
        }
    }
    for(int i=0;i<n;i++){}
    int ans = 0;
    for (int i = 0; i <= 50; i++) {
        for (int j = 0; j <= 50; j++) {
            ans = (ans + dp[n-1][i][j]) % mod;
        }
    }
    return ans;
}
};
