int mod=1e9+7;
class Solution {
public:
    vector<vector<int>> dp;
    int rec(int idx,vector<int> &nums,int prev){

        if(idx==nums.size())return 1;
        if(dp[idx][prev]!=-1)return dp[idx][prev];
        int ans=0;
        if(idx==0){
            for(int i=0;i<=nums[idx];i++){
                ans+=rec(idx+1,nums,i);
                ans%=mod;
            }
        }
        else{

            for(int i=0;i<=nums[idx];i++){
                if(prev<=i&&nums[idx]-i<=nums[idx-1]-prev){
                    ans+=rec(idx+1,nums,i);
                    ans%=mod;
                }
            }
        }

        return dp[idx][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
           int n=nums.size();
           dp=vector<vector<int>> (n,vector<int>(55,-1));
           int ans =rec(0,nums,0);
           return ans;
    }
};
