class Solution {
public:
    int dp[2001][54][54];
    int mod = 1e9+7;
    int solve(int curr1,int prev1,int idx,vector<int> &nums){
        if(idx >= nums.size()){
            return 1;
        }
        if(dp[idx][curr1+1][prev1+1] != -1){
            return dp[idx][curr1+1][prev1+1];
        }
        int ans = 0;
        if(prev1 == -1){
            for(int i = 0;i <= nums[idx];i++){
                ans += solve(nums[idx]-i,i,idx+1,nums);
                ans %= mod;
            }
        }else{
            for(int i = 0;i <= nums[idx];i++){
                if(i <= prev1 && nums[idx]-i >= curr1){
                    ans += solve(nums[idx]-i,i,idx+1,nums);
                    ans %= mod;
                }
            }
        }
        return dp[idx][curr1+1][prev1+1] = ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
            memset(dp,-1,sizeof(dp));
           return solve(-1,-1,0,nums); 
    }
};
