class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        using ll = long long;
        const int mod = 1e9 + 7;
        int lst = 1e9;
        vector<ll> dp(1001);
        dp[0] = 1;
        for (int x : nums) {
            vector<ll> pref = dp;
            for (int i = 1; i <= 1000; i++)
                (pref[i] += pref[i - 1]) %= mod;

            vector<ll> ndp(1001);
            for (int j = 0; j <= x; j++) {
                int k = x - j;
                int r = min(j, lst - k);
                ndp[j] = (r >= 0 ? pref[r] : 0);
                //cout << dp[j] << " \n"[j == x];
            }
            lst = x;

            swap(dp, ndp);
        }

        ll ans = 0;
        for (int i = 0; i <= 1000; i++)
            (ans += dp[i]) %= mod;
        return ans;
    }
};
