class Solution {
public:
  int countOfPairs(vector<int>& nums) {

    int n = nums.size();
    long long ans = 0, M = 1e9 + 7;

    vector<vector<long long>> dp(n + 1, vector<long long> (2005));

    vector<long long> pref(2005);

    auto get = [&](int l, int r) {
      return pref[r] - (l ? pref[l - 1] : 0LL);
    };

    for(int i = n; i >= 0; i--) {
      for(int prev = 0; prev <= 2000; prev++) {

        if(i == n) {
          dp[i][prev] = 1;
          continue;
        }

        long long ans = 0;

        int a = prev, b = (i ? nums[i - 1] - prev: 2000);

        int l = prev, r = nums[i], x = -1;

        while(l <= r) {
          int m = (l + r)/2;

          int cura = m, curb = nums[i] - m;

          if(cura >= a && curb <= b) x = m, r = m - 1;
          else l = m + 1;
        }

        if(x != -1) {

          ans = (ans + get(x, nums[i])) % M;

        }

        dp[i][prev] = ans;

      }

      for(int prev = 0; prev <= 2000; prev++) pref[prev] = dp[i][prev] + (prev ? pref[prev - 1] : 0);

    }

    return dp[0][0];

  }
};
