#include "bits/stdc++.h"
using namespace std;
#define ll long long
const int MOD = 1e9 + 7;

void add(int &x, int y) {
	x = (x + y) % MOD;
}

int add2(int x, int y) {
	return (x + y) % MOD;
}

class Solution {
public:
	int countOfPairs(vector<int>& a) {
		int n = a.size(), mx = *max_element(a.begin(), a.end());
		vector dp(n, vector (mx + 1, 0));
		for(int j = 0; j <= min(a[0], mx); j++) dp[0][j] = 1;
		for(int i = 1; i < n; i++) {
			vector pre(mx + 1, 0);
			pre[0] = dp[i - 1][0];
			for(int j = 1; j <= a[i - 1]; j++) pre[j] = add2(pre[j - 1], dp[i - 1][j]);

			for(int x = 0; x <= min(a[i], mx); x++) {
				int r = min(x, a[i - 1]);
				r = min(r, a[i - 1] - a[i] + x);

				if(r >= 0) add(dp[i][x], pre[r]);
			}
		}
		int ans = 0;
		for(int j = 0; j <= mx; j++) add(ans, dp[n - 1][j]);
		return ans;
	}
};

