class Solution:
    def countOfPairs(self, ar: List[int]) -> int:
        MOD = 10**9+7
        @cache
        def pre(i, a):
            if a > ar[i-1]: return 0
            return (sol(i, a) + pre(i, a+1)) % MOD
        @cache
        def sol(i, a):
            if i == len(ar): return 1
            b = ar[i-1]-a if i else inf
            res = pre(i+1, max(a, ar[i]-b))
            return res%MOD

        res = sol(0, 0)
        pre.cache_clear()
        sol.cache_clear()

        return res
