#define ll long long

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        ll mx = 1000, n = nums.size(), MOD = 1e9 + 7;
        vector<vector<ll> > dp(n + 1, vector<ll> (mx + 2, 0)), dp1;
        // dp[0] = vector<ll> (mx + 2, 1);
        dp1 = dp;
        dp[0][mx] = 1;
        dp1[0][0] = 1;
        for(int i = 0; i < n; i++) {
            ll sum1 = 0, sum2 = 0;
            for(int j = mx; j > nums[i]; j--) {
                (sum1 += dp[i][j]) %= MOD;
            }

            for(int x = 0, y = nums[i]; x <= nums[i]; x++, y--) {
                sum1 += dp[i][y];
                sum2 += dp1[i][x];
                dp[i + 1][y] = min(sum1, sum2) % MOD;
                dp1[i + 1][x] = min(sum1, sum2) % MOD;
            }
        }

        ll ans = 0;
        for(int i = 0; i <= mx; i++) {
            (ans += dp[n][i]) %= MOD;
        }
        // for(int i = 0; i <= n; i++) {
        //     for(int j = 0; j <= mx; j++) {
        //         cout << dp[i][j] << " ";
        //     }
        //     cout << "\n";
        // }
        // cout << "---------\n";
        // for(int i = 0; i <= n; i++) {
        //     for(int j = 0; j <= mx; j++) {
        //         cout << dp1[i][j] << " ";
        //     }
        //     cout << "\n";
        // }

        return ans;
    }
};
