ll addmod(ll a, ll b) {
	a %= mod;
	b %= mod;

	return (a + b) % mod;
}

ll submod(ll a, ll b) {
	a %= mod;
	b %= mod;
	ll x = (a - b);
	return (x % mod + mod) % mod;
}

class Solution {
public:
	int countOfPairs(vector<int>& nums) {
		int n = sz(nums);

		int mx = 0;
		for (auto &i : nums) mx = max(mx, i);

		vector<ll> dp(mx + 2, 0);

		fo(i, 0, nums[0]) dp[i] = 1;
		ll ans = 0;

		fo(pos, 1, n - 1) {
			vector<ll> temp(mx + 2, 0);

			fo(lastVal, 0, nums[pos - 1]) {
				int x1 = lastVal, y1 = nums[pos];
				int x2 = 0, y2 = nums[pos - 1] - lastVal;
				int x21 = nums[pos] - y2, y21  = nums[pos];
				int x = max(x1, x21), y = min(y1, y21);
				if (x <= y) {
					temp[x] = addmod(temp[x], dp[lastVal]);
					temp[y + 1] = submod(temp[y + 1], dp[lastVal]);
				}
			}

			fo(val, 1, mx + 1) temp[val] = addmod(temp[val], temp[val - 1]);

			dp = temp;
		}

		fo(i, 0, nums[n - 1]) {
			ans = addmod(ans, dp[i]);
		}


		return ans;
	}
};
