constexpr static int mod = 1e9 + 7;

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        const int n = a.size();
        const int V = 1000;
        std::vector<std::vector<int>> dp(n, std::vector<int>(V + 1));
        for (int i = 0; i <= V; ++i) {
            dp[0][i] = i > a[0] ? a[0] + 1 : i + 1;
        }
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= a[i]; ++j) {
                int k = std::min(j, j - a[i] + a[i - 1]);
                if (k >= 0) {
                    int delta = dp[i - 1][k];
                    dp[i][j] = (dp[i][j] + delta) % mod;
                }
            }

            for (int j = 1; j <= V; ++j) dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod;
        }

        return dp.back().back();
    }
};
