using ll = long long;
const ll MOD = 1e9+7;

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<ll> dp(1002,0);
        for (int i = 0; i <= nums[0]; ++i) dp[i] = 1;
        for (int i = 1; i < nums.size(); ++i) {
            vector<ll> diff(1002,0);
            for (int pa = 0; pa <= nums[i]; ++pa) {
                int pb = nums[i-1]-pa;
                int t = max(pa, nums[i]-pb);
                if (t <= nums[i]) {
                    diff[t] += dp[pa];
                    diff[nums[i]+1] -= dp[pa];
                }
            }
            for (int j = 0; j < 1001; ++j) {
                diff[j+1] += diff[j];
                while (diff[j] < 0) diff[j] += MOD;
                diff[j] %= MOD;
            }
            swap(diff,dp);
        }
        ll ans = accumulate(dp.begin(), dp.end(), 0LL);
        return ans%MOD;

    }
};
