class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1000000007;
        int n = (int)nums.size();
        vector<vector<long long>> dp(n, vector<long long>(1001));
        for (int i = 0; i <= nums[0]; i++){
            dp[0][i] = 1;
        }
        for (int i = 1; i <= 1000; i++){
            dp[0][i] = dp[0][i] + dp[0][i - 1];
        }

        for (int i = 1; i < n; i++){
            for (int i1 = 0; i1 <= nums[i]; i1++){
                int r = min(i1, nums[i - 1] - nums[i] + i1);
                if (r >= 0) dp[i][i1] = dp[i - 1][r];
            }
            for (int j = 1; j <= 1000; j++){
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod;
            }
        }

        return int(dp.back().back());
    }
};
