class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int mx = *max_element(nums.begin(), nums.end());
        int dp[2][mx+1];
        memset(dp, 0, sizeof(dp));
        int T = 0, F = 1;
        for(int i = 0; i <= nums[0]; i++) {
            dp[T][i] = 1;
        }
        for(int i = 1; i < nums.size(); i++) {
            swap(T, F);
            memset(dp[T], 0, sizeof(dp[T]));
            int diff = max(0, nums[i] - nums[i-1]);
            int sum = 0;
            for(int j = 0; j <= nums[i-1] && j+diff <= nums[i]; j++) {
                sum = (sum + dp[F][j]) % 1000000007;
                dp[T][j+diff] = sum;
                // cout << sum << ' ';
            }
            // cout << endl;
        }
        int res = 0;
        for(int i = 0; i <= nums.back(); i++) {
            res = (res + dp[T][i]) % 1000000007;
        }
        return res;
    }
};
