class Solution {
public:
    int MOD = 1000000000 + 7;
    int dp[2010][1010] = {0};
    int sdp[2010][1010] = {0};
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        nums.insert(nums.begin(), 1005);

        for (int x = 0; x <= nums[1]; x ++)
            dp[1][x] = 1, sdp[1][x] = (dp[1][x] + (x > 0 ? sdp[1][x - 1] : 0)) % MOD;

        for (int k = 2; k <= n; k ++)
        {
            int dmin = max(0, nums[k] - nums[k - 1]);
            for (int x = dmin; x <= nums[k]; x ++)
            {
                //dp[k][x];
                // x >= arr1[k - 1]
                // nums[k] - x <= arr2[k - 1]
                int dx = min(x, nums[k - 1] + x - nums[k]);
                if (dx >= 0)
                {
                    dp[k][x] = sdp[k - 1][dx];
                }
/*
                for (int x0 = 0; x0 <= dx; x0 ++)
                {
                    //if (nums[k] - x <= nums[k - 1] - x0)
                    //if (x0 <= nums[k - 1] + x - nums[k])
                        dp[k][x] = (dp[k][x] + dp[k - 1][x0]) % MOD;
                }*/

                sdp[k][x] = dp[k][x];
                if (x > 0) sdp[k][x] = (sdp[k][x] + sdp[k][x - 1]) % MOD;
            }
        }

        long long ans = sdp[n][nums[n]] % MOD;
        //for (int x = 0; x <= nums[n]; x ++) ans = (ans + dp[n][x]) % MOD;
        return ans;
    }
};
