class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        vt<vt<ll>>dp(1001 , vt<ll>(n , 0));
        rep(i , 0 , a[0]+1){
            dp[i][0] = i+1;
        }
        rep(i , 1 , n){
            int val = max( 0 , a[i]-a[i-1]);
            rep(j , 0 , a[i]+1){
                dp[j][i] =( ( j -val>=0 ) ? (dp[j-val][i-1])  : 0);

                if(j > 0){
                    dp[j][i] += dp[j-1][i] % mod; dp[j][i] %= mod;
                }
            }
            rep(j , a[i]+1 , 1000){
                if(j > 0){
                    dp[j][i] += dp[j-1][i] % mod; dp[j][i] %= mod;
                }
            }
        }
        return dp[a[n-1]][n-1];
    }
};
