class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        dp = [1] * (nums[0] + 1) + [0] * (50 - nums[0])

        for x, prev in zip(nums[1:], nums):
            temp = [0] * 1001

            right = min(x, prev)

            psum = sum(dp[:right + 1])
            for idx in range(x, -1, -1):
                temp[idx] = psum
                if right >= 0:
                    psum -= dp[right]
                    right -= 1

            dp = [v % mod for v in temp]

        return sum(dp) % mod
