class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1e9+7;
        int n = nums.size();
        vector<vector<long long>>dp(n+1,vector<long long>(1001,0));
        for(long long i=0;i<=nums[0];i++){
            long long val1 =  i;
            long long val2 = nums[0]-i;
            dp[0][val1] = 1;
        }
        for(int i=1;i<=1000;i++){
            dp[0][i]+=dp[0][i-1];
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                long long val1 = j;
                long long val2 = nums[i]-j;
                long long k = min(nums[i-1]-val2,val1);
                if(k<0) continue;
                dp[i][j] = dp[i-1][k];
            }
            for(int j=1;j<=1000;j++){
                dp[i][j]=(dp[i][j]+dp[i][j-1])%mod;
            }
        }
        return dp[n-1][1000]%mod;
    }
};
