mod = 10 ** 9 + 7
fmax = lambda x, y: x if x > y else y
class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [1] * (nums[0] + 1)
        for v in nums[1:]:
            ndp = [0] * (v + 2)
            for i in range(len(dp)):
                l = fmax(i, v - (len(dp) - 1 - i))
                r = v
                if l <= r:
                    ndp[l] += dp[i]
                    ndp[r+1] -= dp[i]
            dp = ndp
            for i in range(v + 1):
                dp[i+1] += dp[i]
                dp[i+1] %= mod
            dp.pop()
        return sum(dp) % mod
