#define MOD 1000000007

class Solution {
public:
    int countOfPairs(vector<int>& v) {
        int n = v.size();
        vector<vector<int>> dp(n, vector<int>(1002));
        for (int i = 0; i <= 1000; i++)
            dp[0][i + 1] = dp[0][i] + (i <= v[0]);
        for (int i = 0; i + 1 < n; i++) {
            vector<int> tv(1002);
            for (int j = 0; j <= 1000; j++) {
                int ta = j + max(0, v[i + 1] - v[i]);
                int tb = v[i + 1];
                if (ta <= tb) {
                    int tc = (dp[i][j + 1] + MOD - dp[i][j]) % MOD;
                    (tv[ta] += tc) %= MOD;
                    (tv[tb + 1] += MOD - tc) %= MOD;
                }
            }
            int tmp = 0;
            for (int j = 0; j <= 1000; j++) {
                (tmp += tv[j]) %= MOD;
                dp[i + 1][j + 1] = (dp[i + 1][j] + tmp) % MOD;
            }
        }
        // for (int i = 0; i < n; i++) {for (int j = 0; j <= 20; j++) printf("%d ", dp[i][j]); puts("");}
        return dp.back().back();
    }
};
