public class Solution {
    public int CountOfPairs(int[] nums) {
        const int mod = 1_000_000_007;

        int[,] dp = new int[nums.Length, 1001];
        for (int i = 0; i <= nums[^1]; ++i) 
        {
            dp[nums.Length - 1, i] = 1;
        }

        for (int i = nums.Length - 2; i >= 0; --i) 
        {
            int nextM = nums[i + 1];
            int[] sums = new int[nums[i + 1] + 1 + 1];
            for (int j = nums[i + 1]; j >= 0; --j) 
            {
                sums[j] = (sums[j + 1] + dp[i + 1, j]) % mod;
            }

            for (int j = 0; j <= nums[i]; ++j) 
            {
                int ind = Math.Max(j, nums[i + 1] - (nums[i] - j));
                dp[i, j] = ind < 0 || ind >= sums.Length ? 0 : sums[ind];
            }
        }

        return (int) (Enumerable.Range(0, nums[0] + 1).Sum(cnt => (long) dp[0, cnt]) % mod);
    }
}
