const int MOD = 1e9 + 7;
class Solution {
public:
    int countOfPairs(vector<int>& A) {
        int n = A.size();
        vector<vector<int>> dp(n, vector<int>(1002, 0));
        vector<vector<int>> prefix(n, vector<int>(1002, 0));

        for (int j = 0; j <= A[0]; ++j) {
            dp[0][j] = 1;
        }
         for (int j = 0; j <= 1000; ++j) {
            prefix[0][j] = dp[0][j];
            if (j > 0) {
                prefix[0][j] = (prefix[0][j] + prefix[0][j-1]) % MOD;
            }
        }

        for (int i = 1; i < n; ++i) {
            int cur = A[i];
            for (int j = 0; j <= cur; ++j) {
                int k = min(j, j - (A[i] - A[i - 1]));
                if(k < 0) {
                    continue;
                }
                dp[i][j] = prefix[i - 1][k];
            }
             for (int j = 0; j <= 1000; ++j) {
                prefix[i][j] = dp[i][j];
                if (j > 0) {
                    prefix[i][j] = (prefix[i][j] + prefix[i][j-1]) % MOD;
                }
            }

        }

//         for(int i = 0; i < n; ++i) {
//             for(int j = 0; j < 5; ++j) {
//                 cout << dp[i][j] << " ";
//             }
//             cout << endl;
//         }
//         cout << endl;



//         for(int i = 0; i < n; ++i) {
//             for(int j = 0; j < 5; ++j) {
//                 cout << prefix[i][j] << " ";
//             }
//             cout << endl;
//         }
//         cout << endl;

        int ans = 0;
        for (int j = 0; j <= A[n-1]; ++j) {
            ans = (ans + dp[n-1][j]) % MOD;
        }
        return ans;
    }
};
