class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int md = 1e9 + 7;
        int n = nums.size();
        int cnt = 0;
        vector<vector<int>> dp(n, vector<int>(1001, 0));// a[i] = j cnt
        for(int i = 0; i < n; i++) {
            for(int j = 0; j <= 1000; j++) {
                if(i == 0) {
                    dp[i][j] = (nums[i] >= j)? 1:0;
                    if(j > 0) dp[i][j] += dp[i][j-1];
                }
                else if(j <= nums[i]) {
                    int prev = nums[i-1];
                    // for(int k = 0; k <= j; k++) {
                    //     if(prev - k >= 0 && nums[i] - j <= prev - k) {
                    //         dp[i][j] += dp[i-1][k];
                    //         dp[i][j] %= md;
                    //     }
                    // }
                    int limit = min(j, min(prev, prev + j - nums[i]));
                    // cout << limit << endl;
                    dp[i][j] += (limit >= 0)? dp[i-1][limit] : 0;
                    dp[i][j] %= md;
                    dp[i][j] += (j == 0)? 0: dp[i][j-1];
                    dp[i][j] %= md;
                }
                else {
                    dp[i][j] = dp[i][j-1];
                }
            }
        }
        return dp[n-1][1000];
    }
};
