class Solution {
public:
    int mem[2000][1001];
    const int m=1e9+7;
    int countOfPairs(vector<int>& nums) {
        //nums=vector<int>(2000,1000);
        int n=nums.size();
        for(int i=1;i<n;++i)fill(mem[i],mem[i]+1001,0);
        for(int j=0;j<=nums[0];++j)mem[0][j]=j+1;
        for(int i=1;i<n;++i){
            for(int j=0;j<=nums[i];++j){
                int k=min(j,nums[i-1]-nums[i]+j);
                if(j>0)mem[i][j]=mem[i][j-1];
                if(k>=0)mem[i][j]+=mem[i-1][k];
                if(mem[i][j]>=m)mem[i][j]-=m;
               // cout<<"mem"<<i<<' '<<j<<' '<<mem[i][j]<<endl;
            }
        }
        return mem[n-1][nums[n-1]];
    }
};
