class Solution {
public:

    int dp[2005][1005];
    int pre[2005][1005];

    int countOfPairs(vector<int>& nums) {
        for (int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1;
        }
        pre[0][0] = dp[0][0];
        for (int j = 1; j < 1005; ++j) {
            pre[0][j] =  pre[0][j-1] + dp[0][j];
            pre[0][j] %= 1000000007;
        }
        const int n = nums.size();
        for (int i = 1; i < nums.size(); ++i) {
            int diff = nums[i] - nums[i-1];
            for (int j = nums[i]; j - diff >= 0; --j) {
                int rr = min(j, j - diff);
                // cout << i << ' ' << j << ' ' << rr << endl;
                if (rr >= 0)
                    dp[i][j] = pre[i-1][rr];
            }
            pre[i][0] = dp[i][0];
            for (int j = 1; j < 1005; ++j) {
                pre[i][j] =  pre[i][j-1] + dp[i][j];
                pre[i][j] %= 1000000007;
            }
        }
        // for (int i = 0; i < n; ++i) {
        //     for (int j = 0; j < 5; ++j) {
        //         cout << dp[i][j] << ' ';
        //     }
        //     cout << endl;
        // }
        return pre[n-1][1004];
    }
};
