class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        max_val = max(nums)

        dp = [[0] * (max_val + 1) for _ in range(n)]
        for i in range(nums[0] + 1):
            dp[0][i] = 1

        print
        for i in range(1, n):
            curr = nums[i]
            prefix_sums = [0]
            for k in range(0, max_val + 1):
                prefix_sums.append(prefix_sums[-1] + dp[i - 1][k])
            #print(prefix_sums)
            for j in range(0, nums[i] + 1):
                temp = 0
                max_poss = nums[i - 1] - (nums[i] - j)
                if max_poss >= 0:
                    temp = min(max_poss + 1, j + 1)                    
                    dp[i][j] = prefix_sums[temp]
                else:
                    continue





        #print(dp)

        return sum(dp[-1]) % MOD
