class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7
        n = len(nums)
        dp = [[0] * 1001 for _ in range(n)]

        def get(a):
            ret = [a[0]]
            for i in range(1, len(a)):
                ret.append(ret[-1] + a[i])
            return ret

        def cal(a, bound):
            if bound < 0:
                return 0
            return a[bound]

        for j in range(nums[0] + 1):
            dp[0][j] = 1
        prev = get(dp[0])
        for i in range(1, n):
            # print(prev)
            for j in range(nums[i] + 1):
                bound = min(j, nums[i - 1] - nums[i] + j)
                dp[i][j] = cal(prev, bound)
                # print(i, j, bound, dp[i][j])
                dp[i][j] %= mod
            prev = get(dp[i])
        # print(dp)
        return sum(dp[n - 1]) % mod
