class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int mod = 1e9 + 7;
        int n = nums.size();
        int limit = 1000;
        nums.push_back(limit);
        vector<vector<int>>dp(n+1, vector<int>(limit + 2, 0));
        for(int i=0; i<limit+2; i++) {
            dp[0][i] = 1;
        }

        for(int i=0; i<n; i++) {
            vector<int> prefix(limit+3, 0);
            for(int j=0; j<=limit+1; j++) {
                prefix[j+1] = (prefix[j] + dp[i][j]) % mod;
            }
            for(int pre=0; pre<=limit+1; pre++) {
                int r = min(pre, min(nums[i], nums[i] - nums[i+1] + pre));
                if(r >= 0)
                    dp[i+1][pre] = (dp[i+1][pre] + ((prefix[r+1] - prefix[0] + mod) % mod)) % mod;
            }
        }
        return dp[n][limit+1];
    }
};
