class Solution {
    public int countOfPairs(int[] nums) {
        int mx = 0;
        int n = nums.length;
        int mod = 1000000007;
        for (int i = 0; i < n; i++)
            mx = Math.max(mx, nums[i]);
        long [][] dp = new long[n + 10][mx + 10];
        long[] sum = new long[mx + 10];
        for (int i = 0; i <= nums[0]; ++i)
            dp[0][i] = 1;
        sum[0] = 1;
        for (int i = 1; i <= nums[0]; ++i)
            sum[i] = (sum[i - 1] + dp[0][i]) % mod;
        for (int i = 1; i < n; ++i) {
            for (int j = 0; j <= nums[i]; ++j) {
                int tmp = nums[i] - j;
                if (nums[i - 1] - tmp >= 0)
                    dp[i][j] = sum[Math.min(j, nums[i - 1] - tmp)];
            }
            sum[0] = dp[i][0];
            for (int j = 1; j <= mx; ++j)
                sum[j] = (sum[j - 1] + dp[i][j]) % mod;
        }
        return (int)sum[nums[n - 1]];
    }
}
