int dp[2005][1005];
int mod = 1000000007;

class Solution {
public:
    int countOfPairs(vector<int>& a) {
        int n = a.size();
        int i,j,x,y;
        memset(dp,0,sizeof(dp));
        x = a[0];
        int m,prm;
        for(j = 0; j<= x; j++)
        {
            dp[1][j] = 1;
            // prefix sum
            //printf("at start we can put %d by %d ways\n",j,dp[1][j]);
        }
        for(j=1; j<=1000;j++)
            dp[1][j] += dp[1][j-1];
        for(i=2; i<=n; i++)
        {
            // ith position par agar x rakha to
             m = a[i-1];
            prm = a[i-2];
          //  printf("at %d we want to put till %d and previous is %d\n",i,m,prm);
            for(x = 0; x<=m; x++)
            {
                // y = m - x hoga
                // last ke saare jo m1-m+x se bade hain aur x se chhote hain
                int st = 0;
                int en = min(prm - m + x,x); 

               // printf("if we put %d we need all values in last from %d to %d\n",x,st,en);
                if(st <= en)
                {
                    if(st == 0)dp[i][x] += dp[i-1][en];
                    else dp[i][x] += (mod + dp[i-1][en] - dp[i-1][st-1])%mod;
                    if(dp[i][x] >= mod)dp[i][x] -= mod;
                  //  printf("hence become %d\n",dp[i][x]);
                }
            }
            for(j = 1; j<=m; j++){dp[i][j] += dp[i][j-1];dp[i][j]%=mod;}
            for(j = m+1; j<=1000;j++)dp[i][j] = dp[i][j-1];

        }
      //  int ans = 0;

        return dp[n][1000];

    }
};
