class Solution {
    vector<vector<int>> dp;

public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int mod = 1e9 + 7;
        int lim = 1001;

        dp = vector<vector<int>>(n, vector<int>(lim, 0));

        for (int i = 0; i <= nums[0]; i++) {
            dp[0][i] = 1;
        }

        vector<int> cum(lim);

        for (int i = 1; i < n; i++) {

            for (int val = 0; val <= nums[i]; val++) {
                cum[val] = 0;
            }


            for (int prev = 0; prev <= nums[i - 1]; prev++) {
                int cur = prev + max(nums[i] - nums[i - 1], 0);

                if (cur > nums[i]) {
                    break;
                }

                cum[cur] += dp[i - 1][prev];
                cum[cur] %= mod;
            }

            dp[i][0] = cum[0];

            for (int val = 1; val <= nums[i]; val++) {
                dp[i][val] = (dp[i][val - 1] + cum[val]) % mod;
            }
        }

        int ans = 0;

        for (int cur = 0; cur <= nums[n - 1]; cur++) {
            ans += dp[n - 1][cur];
            ans %= mod;
        }

        return ans;

    }
};
