class Solution {
public:
    int countOfPairs(vector<int>& nums) 
    {
        int n=nums.size(),ans=0;
        vector dp(n,vector<int>(1001,0));
        for(int i=0;i<=nums[0];i++)
        {
            dp[0][i]=1;
        }
        for(int len=1;len<n;len++)
        {
            vector<int>suffix=getSuffixSum(dp[len-1]);
            for(int i=0;i<=nums[len];i++)
            {
                int ind=max(i,i+nums[len-1]-nums[len]);
                if(ind>1000)
                {
                    continue;
                }
                dp[len][i]+=suffix[ind];
                dp[len][i]%=MOD;
            }
        }
        for(int i=0;i<=1000;i++)
        {
            ans=(ans+dp[n-1][i])%MOD;
        }
        return ans;
    }
private:
    const int MOD=1e9+7;
    vector<int> getSuffixSum(const vector<int>&vec)
    {
        int n=vec.size();
        vector<int>suffix(n+1,0);
        for(int i=n-1;i>=0;i--)
        {
            suffix[i]=(suffix[i+1]+vec[i])%MOD;
        }
        return suffix;
    }
};
