class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)
        ma = max(nums)
        dp = [0] * (ma + 1)
        for j in range(nums[0] + 1):
            dp[j] = 1
        for i in range(1, n):
            ndp = [0] * (ma + 1)
            cum = 0
            pb = -50000
            #print(dp)
            for j in range(nums[i] + 1):
                ub = min(j + nums[i - 1] - nums[i], j)
                ub = min(ub, ma)
                if ub >= 0:
                    if pb == -50000:
                        #print('here', ub, dp)
                        for _ in range(ub + 1):
                            cum += dp[_]
                        pb = ub
                    else:
                        for _ in range(pb + 1, ub + 1):
                            cum += dp[_]
                        pb = ub

                ndp[j] += cum
                ndp[j] %= MOD
            dp = ndp
        result = sum(dp[j] for j in range(nums[-1] + 1)) % MOD
        return result
