class Solution {
public:
    #define ll long long
    #define mod 1000000007

    int countOfPairs(vector<int>& a) {
        int n = a.size() ;
        vector<vector<int>> dp(n + 1, vector<int>(1001, 0));

        for(int i = 0 ; i <= 1000; i ++) dp[0][i] = 1 ;

        for(int i = 1 ; i <= n; i ++){
            for(int j = 0 ;  j <= 1000; j ++){
                int el = a[i - 1] - j ;
                if(el < 0) break;

                int d = 1e9 , last = (i > 1 ? a[i - 2] : 1e9);
                if(i > 1) d = j - a[i - 1] + a[i - 2] ;

                int lb = min({last, d, j});
                // cout << lb << ' ' ;

                if(lb >= 0){
                    dp[i][j] += dp[i - 1][lb] ;
                    dp[i][j] %= mod ;
                }

//                 for(int k = 0 ; k <= min({last, d, j}); k ++){
//                     int prev = 1e9 ;
//                     if(i > 1) prev = a[i - 2] - k ;

//                     if(prev < el) continue;

//                     dp[i][j] += dp[i - 1][k] ;
//                     dp[i][j] %= mod ;
//                 }
            }
            // cout << endl;

            for(int j = 1; j <= 1000; j ++){
                dp[i][j] = (dp[i][j] + dp[i][j - 1]) % mod ;
            }
        }

//         int ans = 0 ;
//         for(int i = 0 ; i <= 1000; i ++) ans = (ans + dp[n][i]) % mod ;

        return dp[n][1000] ;
    }
};
